mapper.update:
UPDATE order_sub 
SET o_count=#{o_count},
	o_sub_total_price=#{o_count}*o_sub_price
WHERE o_no=#{o_no} 
AND p_code=#{p_code} 
AND p_no=#{p_no}

mapper.select:
SELECT o_count, o_sub_total_price
FROM order_sub
WHERE o_no=#{o_no} and p_code=#{p_code} and p_no=#{p_no}

SELECT name,phone,addr
FROM users
WHERE mem_num = #{mem_num}
