mapper.select:
SELECT pt.p_code, pk.p_kind_code, pt.p_name, pt.p_brand, pt.p_price, pt.point, pt.p_regdate, pt.p_comment, pt.p_detail_comment, pt.p_main_img, pk.kind_name, pk.parent_kind_code FROM product_tb pt, product_kind pk
pt.p_kind_code = pk.p_kind_code
AND pk.parent_kind_code = #{parent_kind_code}
AND pK.p_kind_code = #{p_kind_code}

SELECT p_sub_img
FROM product_sub_img
WHERE p_code = #{p_code}

SELECT p_main_img
FROM product_tb
WHERE p_code = #{p_code}

SELECT p_name,p_price
FROM product_tb
WHERE p_code = #{p_code}

select p_size 
from product_sub
where p_code = #{p_code}

select p_stock
from product_sub
where p_size = #{p_size}
